package worder.core

fun Number.formatGrouped(): String {
    val num = this.toString()
    val len = num.length

    if (len < 4)
        return num

    val res = StringBuilder(len + len / 3)
    res.append(num.substring(0, len % 3))

    for (i in (len % 3) until len step 3) {
        res.append(" " + num.substring(i, i + 3))
    }

    return res.toString()
}
