class WorderDefaultStyles : Stylesheet() {
    companion object {
        val statsBlock by cssclass()
        val insertUnit by cssclass()
        val insertBatch by cssclass()
        val unitButton by cssclass()
        val dragDropField by cssclass()
    }


    init {
        text {
            fontSize = 12.px
        }

        imageView and disabled {
            opacity = 0.2
        }

        scrollPane {
            backgroundColor += Color.WHITE
            viewport {
                backgroundColor += Color.WHITE
            }
        }

        form {
            backgroundColor += Color.WHITE
        }

        titledPane {
            title {
                padding = box(8.px)
                backgroundInsets += box(0.px)
                backgroundRadius += box(0.px)
            }
        }

        button {
            padding = box(8.px)
            backgroundInsets += box(0.px)
            backgroundRadius += box(0.px)
        }

        comboBox {
            backgroundInsets += box(0.px)
            backgroundRadius += box(0.px)
        }

        statsBlock {
            alignment = Pos.TOP_CENTER
            padding = box(15.px)
            borderRadius += box(10.px)
            borderColor += box(Color.GRAY)
        }

        insertUnit {
            padding = box(10.px)
            borderRadius += box(10.px)
            borderColor += box(Color.GRAY)

        }

        insertBatch {
            text {
                fontSize = 18.px
            }

//            separator {
//                padding = box(60.px, 0.px)
//
//                line {
//                    effect = DropShadow(2.0, Color.DIMGRAY)
//                }
//            }
        }

        dragDropField {
            backgroundColor += c(Color.GRAY.toString(), 0.1)
            borderColor += box(Color.DARKGRAY)
            borderStyle += BorderStrokeStyle(
                    StrokeType.OUTSIDE,
                    StrokeLineJoin.MITER,
                    StrokeLineCap.BUTT,
                    10.0,
                    0.0,
                    listOf(20.0, 14.0)
            )
        }

        drawer {
            backgroundImage += javaClass.getResource("/images/airplane.png").toURI()!!
            backgroundRepeat += BackgroundRepeat.NO_REPEAT to BackgroundRepeat.NO_REPEAT
            backgroundPosition += BackgroundPosition.CENTER

            drawerItem {
                backgroundColor += Color.WHITE
